import React, { useState, useEffect } from 'react';
// @ts-ignore
import Button from '../Elements/Button.tsx'

const RecurranceView = (props) => {
    const [toggleDaysUntilNextBuy, setToggleDaysUntilNextBuy] = useState(true)

    function manageToggleDaysUntilNextBuy(){
        setToggleDaysUntilNextBuy(!toggleDaysUntilNextBuy)
    }
    return (
        <div>
            {/* <div className="m-5 flex 100vw text-sm">
                <p>Next buying list on: </p>
                <p>30/01/2023</p>
            </div> */}
            <div className="m-5 flex-column 100vw">
                <h2>Today's buying list</h2>
                <div className="flex">
                    <h2 className="mr-2"><strong>{props.daysUntilNextBuy}</strong> days until next buy</h2>
                    {
                        toggleDaysUntilNextBuy? <Button onClick={manageToggleDaysUntilNextBuy} text="edit" class="button-generic-small"/> : <Button text="save" class="button-generic-small"  onClick={manageToggleDaysUntilNextBuy}/>
                    }
                </div>
            </div>
            <table className='m-5 min-w-[90%]'>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Current inventory</th>
                        <th>Remaining days</th>
                    </tr>
                </thead>
                <tbody>
                    {props.rows}
                </tbody>
            </table>
        </div>
    )
}

export default RecurranceView
